import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Cell({value, onClick}) {
  return (
      <button className="cell" onClick={onClick}>
        {value}
      </button>
  )
}

function GameBoard () {
  const[cells, setCell] = useState(Array(42).fill(null));
  const[isBlueTurn, setBlueTurn] = useState(true);
  const winner = calculateWinner(cells);

    return (
      <div>
        <div>
          {renderCell(0)}
          {renderCell(1)}
          {renderCell(2)}
          {renderCell(3)}
          {renderCell(4)}
          {renderCell(5)}
          {renderCell(6)}
        </div>
        <div>
          {renderCell(7)}
          {renderCell(8)}
          {renderCell(9)}
          {renderCell(10)}
          {renderCell(11)}
          {renderCell(12)}
          {renderCell(13)}
        </div>
        <div>
          {renderCell(14)}
          {renderCell(15)}
          {renderCell(16)}
          {renderCell(17)}
          {renderCell(18)}
          {renderCell(19)}
          {renderCell(20)}
        </div>
        <div>
          {renderCell(21)}
          {renderCell(22)}
          {renderCell(23)}
          {renderCell(24)}
          {renderCell(25)}
          {renderCell(26)}
          {renderCell(27)}
        </div>
        <div>
          {renderCell(28)}
          {renderCell(29)}
          {renderCell(30)}
          {renderCell(31)}
          {renderCell(32)}
          {renderCell(33)}
          {renderCell(34)}
        </div>
        <div>
          {renderCell(35)}
          {renderCell(36)}
          {renderCell(37)}
          {renderCell(38)}
          {renderCell(39)}
          {renderCell(40)}
          {renderCell(41)}
        </div>
        <div className="game-info">{getStatus()}</div>
      </div>
    )

    function renderCell(val) {
      return (
        <Cell value = {cells[val]} onClick={() => {
          const duplicateCells = cells.slice();

          if (duplicateCells[val]===null) {
            duplicateCells[val] = isBlueTurn ? 'X' : 'O';
            setCell(duplicateCells);
            setBlueTurn(!isBlueTurn)
          }

        }}/>
      )
    }

    function getStatus() {
      if (winner) {
        return "Winner: " + winner;
      }
    }
}

ReactDOM.render(
    <GameBoard/>,
  document.getElementById('root')
);

function calculateWinner(cells) {
  const posCombos = [
    [0,1,2,3],[1,2,3,4],[2,3,4,5],[3,4,5,6],[4,5,6,7],[5,6,7,8], 
  ];

  for (let i = 0; i < posCombos.length; i++) {
    const [a, b, c] = posCombos[i];
    if (cells[a] && cells[a] === cells[b] && cells[a] === cells[c]) {
      return cells[a];
    }
  }
  return null;
}
